mapper.insert:
select nextval('hpa_user_seq')
insert into hpa_user (user_id, email, password, password_updated_at, nickname, name, mobile_number, gender, is_closed, member_ty_code, member_ci, pass_fail_cnt, admin_nickname, birthday, mylgid_join_yn, affmemcd, qr_value, pricert_yn, reg_id, created_at, mod_id, updated_at) values (#{userId}, #{email}, #{password}, #{passwordUpdatedAt}, #{nickname}, #{name}, #{mobileNumber}, #{gender}, #{isClosed}, #{memberTyCode}, #{memberCi}, #{passFailCnt}, #{adminNickname}, #{birthday}, #{mylgidJoinYn}, #{affmemcd}, #{qrValue}, #{pricertYn}, #{regId}, #{createdAt}, #{modId}, #{updatedAt}
);
